def get_needed_components_per_roomtype(glb_data_presets, glb_roomtypes):
    # print(glb_data_presets) 
    print("\tCorrelating roomstypes in IFC with Roombooks")
    components_per_room_type = {}
    no_conf = []
    for room_name, room_type in glb_roomtypes.items(): #Roomtypes from IFC Structure
        for config_level in glb_data_presets.keys(): # getting config Level component/Room information
            try:
                for lvl in components_per_room_type[config_level]:
                    pass
            except KeyError:
                components_per_room_type[config_level] = {}
            for room_by_conf_lvl in glb_data_presets[config_level].keys(): #getting the room speciefic components
                if str(room_by_conf_lvl) == str(room_type):    
                    #glb_roomtypes[room_type[0]] = glb_data_presets[config_level][room_type[0]]
                    components_per_room_type[config_level][(room_type, room_name)] = glb_data_presets[config_level][room_by_conf_lvl]
                else:
                    no_conf.append(room_type)
    rooms = ''
    for room in set(no_conf):
        rooms = f'{rooms}, {room}'
    print(f'\t\t- Room(s) {rooms} have no configured components\n')
    #print(components_per_room)
    return components_per_room_type

def get_components_for_components_per_roomtype(glb_components_per_roomtype, glb_components):
    # print(glb_components)
    print("\tGetting Components for Components per Roomtype")
    #print(glb_components_per_roomtype)
    components_per_room = {}
    for config_level, room_types in glb_components_per_roomtype.items():
        components_per_room.setdefault(config_level, {})

        for room_type, components in room_types.items():

            for component in components:

                component_type = component[0][0]
                if component_type == 'Steckdosen':
                    component_type = 'plugs'
                elif component_type == 'Bedienung':
                    component_type = 'switches'
                elif component_type == 'Beleuchtung':
                    component_type = 'lamps'
                else:
                    print(f'\t- no Component found for type: {component_type}')
                # print(component_type)
                component_useability = component[0][1]
                component_name = component[0][2]
                component_amount = component[1]

                for list_components in glb_components[component_type]:
                    # print(list_components)
                    # print(component_useability, component_name)
                    #print(list_components['useability'], component_useability)
                    # result = list_components['useability'].split('/')
                    # # for useability in result:
                    # if component_type == 'plugs':
                    #     print(list_components['useability'], component_useability)
                    #     print(list_components['classification'], component_name)
                    if list_components['useability'] == component_useability and  list_components['classification'] == component_name:
                        # print(f'\t- Component found for useability: {component_useability} and classification: {component_name}')
                        components_per_room[config_level].setdefault(room_type[1], {}).setdefault(component_type, {})
                        components_per_room[config_level][room_type[1]][component_type] = list_components
                        components_per_room[config_level][room_type[1]][component_type]['amount'] = component_amount
                        # else:
                            # print(f'\t- no Component found for useability: {component_useability} and classification: {component_name}')
                        continue

                    
    print(f'\t\t- {len(components_per_room)} Components found\n')
    return components_per_room



